import React, {Component} from 'react';
import { Button} from 'react-bootstrap';
import axios from 'axios';



function updateProduct(produc){
    console.log("function called")
    const name = localStorage.getItem('userid')
    const prod = {name: name,product:produc}
    axios.post('http://localhost:4000/product/update',prod)
         .then(response => {
             console.log(response);
         })
         .catch(function(error) {
             console.log(error);
         })

         window.location = '/';
}


export default class DispatchProduct extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
    }


    componentDidMount() {
        axios.post('http://localhost:4000/product/dispatchproducts',localStorage.getItem('userid'))
             .then(response => {
                 this.setState({products: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }



    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Bundle Quantity</th>
                            <th>Bundly Cost</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.products.map((currentproduct, i) => {
                            return (
                                <tr>
                                    <td>{currentproduct.Name}</td>
                                    <td>{currentproduct.BundleQuantity}</td>
                                    <td>{currentproduct.BundleCost}</td>
                                    <td><Button variant="danger">Dispatch</Button></td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
