import React, {Component} from 'react';
import { Button} from 'react-bootstrap';
import axios from 'axios';



function updateProduct(produc){
    console.log("function called")
    const name = localStorage.getItem('userid')
    const prod = {name: name,product:produc}
    axios.post('http://localhost:4000/product/update',prod)
         .then(response => {
             console.log(response);
         })
         .catch(function(error) {
             console.log(error);
         })

         window.location = '/showproduct';
}



export default class ShowOrder extends Component {

    constructor(props) {
        super(props);
        this.state = {products: []}
    }


    componentDidMount() {
        const prod1 = {name:localStorage.getItem('userid')}
        axios.post('http://localhost:4000/orders/show',prod1)
             .then(response => {
                 this.setState({products: response.data});
                 console.log(response)
             })
             .catch(function(error) {
                 console.log(error);
             })
    }



    render() {
        return (
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Quantity</th>
                            <th>Status</th>
                            <th>Vendor</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.products.map((currentproduct, i) => {
                            return (
                                <tr>
                                    <td>{currentproduct.Name}</td>
                                    <td>{currentproduct.Quantity}</td>
                                    <td>{currentproduct.Status}</td>
                                    <td>{currentproduct._id}</td>
                                    <td><Button variant="primary" onClick={() => updateProduct(currentproduct._id)}>Rate & Review</Button></td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
